/*The stars are out there. I'm going to fix the celestial sphere at 20 AU. 1 AU = 149,597,870.7km149597870700 m20 AU  = 2 991 957 414 000 mthree trillion meters. */#import "star.h"@implementation star- (id)init {	if ((self = [super init])) {        [self reset:0:0.0:0.0:2.0]; 	}		return self;}- (void)reset:(int)c:(float)r:(float)d:(float)m {    ra = r;     dc = d;     mag = m;     con = c;         p.x = -STAR_FAR * cos(dc) * cos(ra);     p.y = STAR_FAR * sin(dc);     p.z = STAR_FAR * cos(dc) * sin(ra); }- (float)getMag { return mag; }- (int)getCon { return con; }- (void)getVec:(mVec3 *)pout { mvCopy(pout, &p); }@end